package cn.ecomb.common.utils.util;


import org.springframework.util.Base64Utils;

import javax.crypto.*;
import javax.crypto.spec.GCMParameterSpec;
import javax.crypto.spec.SecretKeySpec;
import java.security.InvalidAlgorithmParameterException;
import java.security.InvalidKeyException;
import java.security.Key;
import java.security.NoSuchAlgorithmException;
import java.util.HashMap;
import java.util.Map;
import java.util.Objects;

/**
 * aes-gcm加解密
 *
 * @author jaffee
 */
public class AesGcmCipher {

    /**
     * Default nonce size in bytes.
     */
    private static final int NONCE_SIZE = 12;

    /**
     * Default authentication tag size in bits.
     */
    private static final int AUTHENTICATION_TAG_SIZE = 128;

    /**
     * AES-GCM cipher.
     */
    private static final String CIPHER_AES_GCM = "AES/GCM/NoPadding";

    private static Map<String, Key> mKeyStore = new HashMap<>();

    private static Key getAesKey(String encryptPass) {
        if (Objects.isNull(mKeyStore.get(encryptPass))) {
            mKeyStore.put(encryptPass, createAesKey(encryptPass));
        }
        return mKeyStore.get(encryptPass);
    }

    /**
     * Create an AES key for both encryption and decryption with AES-GCM cipher. The key requires
     * the device to be unlocked for decryption.
     *
     * @return a new AES key
     */
    private static Key createAesKey(String encryptPass) {
        try {
            SecretKeySpec keySpec = new SecretKeySpec(encryptPass.getBytes("utf-8"), "AES");
            return keySpec;
        } catch (Exception e) {
            throw new RuntimeException(e);
        }
    }

    public static String doEncrypt(String content, String encryptPass) {
        try {
            byte[] cipherBytes = content.getBytes();
            Cipher cipher = Cipher.getInstance(CIPHER_AES_GCM);
            cipher.init(Cipher.ENCRYPT_MODE, getAesKey(encryptPass));
            byte[] nonce = cipher.getIV();
            byte[] ciphertext = new byte[NONCE_SIZE + cipher.getOutputSize(cipherBytes.length)];
            System.arraycopy(nonce, 0, ciphertext, 0, NONCE_SIZE);
            cipher.doFinal(cipherBytes, 0, cipherBytes.length, ciphertext, NONCE_SIZE);
            return Base64Utils.encodeToString(ciphertext);
        } catch (ShortBufferException | NoSuchAlgorithmException | NoSuchPaddingException
                | InvalidKeyException |
                BadPaddingException | IllegalBlockSizeException e) {
            throw new RuntimeException(e);
        }
    }


    public static String doDecrypt(String content, String encryptPass) {
        try {
            byte[] ciphertext = Base64Utils.decodeFromString(content);
            Cipher cipher = Cipher.getInstance(CIPHER_AES_GCM);
            GCMParameterSpec gcmParameterSpec =
                    new GCMParameterSpec(AUTHENTICATION_TAG_SIZE, ciphertext, 0, NONCE_SIZE);
            cipher.init(Cipher.DECRYPT_MODE, getAesKey(encryptPass),
                    gcmParameterSpec);

            byte[] plaintext = new byte[cipher.getOutputSize(ciphertext.length - NONCE_SIZE)];
            cipher.doFinal(ciphertext, NONCE_SIZE, ciphertext.length - NONCE_SIZE, plaintext, 0);
            return new String(plaintext);
        } catch (ShortBufferException | NoSuchAlgorithmException | NoSuchPaddingException |
                InvalidKeyException | InvalidAlgorithmParameterException |
                BadPaddingException | IllegalBlockSizeException e) {
            throw new RuntimeException(e);
        }
    }


    public static void main(String[] args) {
        String s = "{\"storeCode\":\"Yuj210a246\"}";
        String pass = "d797d0cec33a22912551ae679b54c404";
        String code = "";
        System.out.println("加密之前：" + s);
        System.out.println("加密结果：" + code);
        System.out.println(("解密结果：" + AesGcmCipher.doDecrypt(code, pass)));
    }
}
