mapper.update:
update tbl_order set total_amount = #{totalAmount}, pay_amount = #{payAmount}, freight_amount = #{freightAmount},
pay_type = #{payType}, status = #{status}, delivery_company = #{deliveryCompany}, delivery_id = #{deliveryId},
confirm_status = #{confirmStatus}, payment_time = #{paymentTime}, delivery_time = #{deliveryTime},
receive_time = #{receiveTime}, comment_time = #{commentTime}, modify_time = #{modifyTime}
where order_id = #{orderId}
